import React from 'react';
import { useNavigate } from 'react-router-dom';

const LandingPage = () => {
  const navigate = useNavigate();

  const handlePlayClick = () => {
    navigate('/role-selection');
  };

  return (
    <div 
      className="min-h-screen relative"
      style={{
        backgroundImage: 'url(/assets/images/backgrounds/signup-bg.png)',
        backgroundSize: '100% 100%',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        fontFamily: 'Nunito, sans-serif'
      }}
    >
      {/* Header */}
      <header className="w-full relative">
        <img
          src="/assets/images/navbar/navbar.png"
          alt="EcoSCha Navbar"
          className="w-full h-auto object-cover shadow-lg"
        />
        {/* Hamburger Menu Button */}
        <button className="absolute top-4 right-4 p-2 hover:bg-black/10 rounded-lg transition-colors duration-200 hover:scale-105 transform">
          <img
            src="/assets/images/icons/hamburgerbutton.png"
            alt="Menu"
            className="w-12 h-12 transition-transform duration-200 hover:brightness-110"
          />
        </button>
      </header>

      {/* Main Content */}
      <main className="relative z-10 flex items-center justify-center min-h-[80vh] px-4">
        {/* Play Container with Logo */}
        <div className="flex justify-center">
          <button
            onClick={handlePlayClick}
            className="transition-all duration-300 transform hover:scale-105 hover:shadow-xl"
          >
            <img
              src="/assets/images/container/play-container.png"
              alt="Play Container"
              className="w-auto h-auto max-w-2xl hover:brightness-110"
            />
          </button>
        </div>

            <div className="flex justify-center">
          <button
            onClick={handlePlayClick}
            className="relative group"
          >
            {/* Button Shadow */}
            <div className="absolute top-2 left-0 w-full h-full bg-red-900 rounded-2xl"></div>
            
            {/* Main Button */}
            <div 
              className="relative px-12 py-5 text-white font-bold text-2xl rounded-2xl transition-all duration-200 transform group-hover:translate-y-1 group-active:translate-y-2 flex items-center gap-4 shadow-lg"
              style={{ backgroundColor: '#982827' }}
            >
              {/* Play Icon */}
              <div className="w-0 h-0 border-l-[16px] border-l-white border-t-[12px] border-t-transparent border-b-[12px] border-b-transparent"></div>
              
              {/* Text */}
              <span className="font-bold tracking-wide" style={{ fontFamily: 'Nunito, sans-serif' }}>
                PLAY
              </span>
            </div>
          </button>
        </div>
      </main>

    </div>
  );
};

export default LandingPage;
