import React from 'react';

const VideoLearningPage = () => {
  return (
    <div 
      className="min-h-screen relative"
      style={{
        backgroundImage: 'url(/assets/images/backgrounds/signup-bg.png)',
        backgroundSize: '100% 100%',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}
    >
      {/* Header */}
      <header className="w-full relative">
        <img
          src="/assets/images/navbar/navbar.png"
          alt="EcoSCha Navbar"
          className="w-full h-auto object-cover shadow-lg"
        />
        {/* Logo positioned in navbar */}
        <div className="absolute top-1/2 left-8 transform -translate-y-1/2">
          <img 
            src="/assets/images/logo/ecoscha-logo.png" 
            alt="EcoSCha Logo"
            className="h-12 w-auto"
          />
        </div>
        {/* Hamburger Menu Button */}
        <button className="absolute top-4 right-4 p-2 hover:bg-black/10 rounded-lg transition-colors duration-200 hover:scale-105 transform">
          <img
            src="/assets/images/icons/hamburgerbutton.png"
            alt="Menu"
            className="w-12 h-12 transition-transform duration-200 hover:brightness-110"
          />
        </button>
      </header>

      {/* Main Content */}
      <main className="relative z-10 max-w-5xl mx-auto px-4 py-12 flex flex-col items-center">
        
        {/* Instruction Text Card */}
        <div className="bg-white rounded-3xl shadow-lg p-8 mb-8 max-w-4xl w-full">
          <p className="text-gray-800 text-lg font-poppins text-center leading-relaxed">
            Perhatikan video di bawah ini dengan seksama! Tunggu hingga Sustainability Guide mengaktifkan fase siang.
          </p>
        </div>

        {/* Video Container */}
        <div className="bg-white rounded-3xl shadow-lg p-6 max-w-4xl w-full">
          <div className="relative rounded-2xl overflow-hidden bg-gray-900 aspect-video">
            {/* Video Thumbnail/Background */}
            <img 
              src="/assets/images/backgrounds/video-thumbnail.png"
              alt="Video Thumbnail"
              className="w-full h-full object-cover"
              onError={(e) => {
                // Fallback to a solid color background if image doesn't exist
                e.target.style.display = 'none';
                e.target.parentElement.style.background = 'linear-gradient(135deg, #1e3a8a 0%, #059669 50%, #0891b2 100%)';
              }}
            />
            
            {/* Play Button Overlay */}
            <div className="absolute inset-0 flex items-center justify-center">
              <button className="bg-white bg-opacity-90 rounded-full p-6 shadow-lg hover:bg-opacity-100 transition-all duration-300 transform hover:scale-110">
                <svg 
                  className="w-12 h-12 text-gray-700 ml-1" 
                  fill="currentColor" 
                  viewBox="0 0 24 24"
                >
                  <path d="M8 5v14l11-7z"/>
                </svg>
              </button>
            </div>
          </div>
        </div>

        {/* Additional Info (Optional) */}
        <div className="mt-6 text-center">
          <p className="text-white text-sm font-poppins opacity-80">
            Video akan dimulai secara otomatis
          </p>
        </div>
      </main>

      {/* Background Elements */}
      <div className="absolute bottom-0 left-0 w-full h-32 opacity-20">
        <div className="grid grid-cols-20 h-full">
          {[...Array(60)].map((_, i) => (
            <div 
              key={i}
              className={`${
                i % 2 === 0 ? 'bg-yellow' : 'bg-white'
              } ${
                Math.floor(i / 20) % 2 === 1 ? 'order-1' : ''
              }`}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default VideoLearningPage;
