import React, { useState, useEffect } from 'react';

const ModeratorRoom = () => {
  const [roomCode, setRoomCode] = useState('A3HJGF89');
  const [currentRole, setCurrentRole] = useState('SUSTAINABILITY GUIDE');
  const [gamePhase, setGamePhase] = useState('role-reveal'); // role-reveal, night, day, voting
  const [players, setPlayers] = useState([]);
  const [generatedCode, setGeneratedCode] = useState('');

  // Sample player data
  useEffect(() => {
    setPlayers([
      { id: 1, name: 'Player 1', role: 'Waste Manager', alive: true },
      { id: 2, name: 'Player 2', role: 'Eco Citizen', alive: true },
      { id: 3, name: 'Player 3', role: 'Green Guardian', alive: true },
      // ... more players
    ]);
  }, []);

  const Logo = () => (
    <div className="inline-flex items-center justify-center">
      <img 
        src="/assets/images/logo/ecoscha-logo.png" 
        alt="EcoSCha Logo"
        className="h-12 w-auto"
      />
    </div>
  );

  const generateNewCode = () => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let result = '';
    for (let i = 0; i < 8; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    setGeneratedCode(result);
    setRoomCode(result);
  };

  const RoleCard = ({ role }) => {
    const roleData = {
      'SUSTAINABILITY GUIDE': {
        image: '/assets/images/cards/sustainability-guide.jpg',
        description: `Raka Adalah Sosok Yang Dituakan Di Desa Dayeuhmangguung. Ia Bukan Kepala Desa Secara Jabatan, Tetapi Pangaguing Lembur Penuntun Masyarakat Dalam Nilai-Nilai Kahirupan (Kehidupan) Dan Kelestarian Alam. Sejak Muda, Ia Belajar Banyak Dari Karuhun (Leluhur) Tentang Pentingnya Menjaga Keseimbangan Antara Manusia Dan Alam.

"Alam Téh Sakola Nu Teu Boga Tembok," Katanya Suatu Hari Sambil Mengajarkan Anak-Anak Menanam Pohon Di Lereng Gunung Cikuray. (Alam Itu Sekolah Tanpa Dinding.) Ia Meyakini Bahwa Masalah Seperti Kebakaran Hutan, Krisis Air, Dan Polusi Bukan Hanya Soal Teknis, Tapi Soal Cara Berpikir Dan Cara Hidup.

Sebagai Sustainability Guide, Raka Tidak Ikut Bertarung Atau Bersembunyi Dalam Konflik Antar Peran. Ia Adalah Penuntun. Di Sepanjang Permainan, Ia Membantu Memastikan Nilai-Nilai Luhur Seperti Gotong Royong (Sabilulungan) Dan Peduli Sesama (Silih Asah, Silih Asih, Silih Asuh) Tetap Terjaga Hidup.

Ia Tidak Bisa Mengubah Jalan Cerita, Tapi Bisa Menuntun Arah Langkah. Ia Tahu Bahwa Perubahan Besar Selalu Dimulai Dari Satu Hal Sederhana Yaitu Kesadaran.`
      }
    };

    const data = roleData[role] || roleData['SUSTAINABILITY GUIDE'];

    return (
      <div className="bg-white rounded-2xl p-6 shadow-lg">
        <div className="bg-gradient-to-b from-yellow-400 to-orange-400 rounded-2xl p-4 shadow-lg mb-4">
          <div className="bg-white rounded-xl p-4 mb-3">
            <div className="text-center">
              <div className="text-xs text-green-600 mb-2">+ EcoSCha +</div>
              <div className="w-32 h-40 bg-gray-200 rounded-lg mx-auto mb-2 flex items-center justify-center overflow-hidden">
                <img 
                  src={data.image} 
                  alt={role}
                  className="w-full h-full object-cover rounded-lg"
                />
              </div>
            </div>
          </div>
          
          <div className="bg-gradient-to-r from-green-700 to-green-900 text-white text-center py-2 rounded-xl">
            <div className="font-bold text-sm">{role}</div>
          </div>
        </div>
      </div>
    );
  };

  const RoleDescription = ({ role }) => {
    const roleData = {
      'SUSTAINABILITY GUIDE': {
        description: `Raka Adalah Sosok Yang Dituakan Di Desa Dayeuhmangguung. Ia Bukan Kepala Desa Secara Jabatan, Tetapi Pangaguing Lembur Penuntun Masyarakat Dalam Nilai-Nilai Kahirupan (Kehidupan) Dan Kelestarian Alam. Sejak Muda, Ia Belajar Banyak Dari Karuhun (Leluhur) Tentang Pentingnya Menjaga Keseimbangan Antara Manusia Dan Alam.

"Alam Téh Sakola Nu Teu Boga Tembok," Katanya Suatu Hari Sambil Mengajarkan Anak-Anak Menanam Pohon Di Lereng Gunung Cikuray. (Alam Itu Sekolah Tanpa Dinding.) Ia Meyakini Bahwa Masalah Seperti Kebakaran Hutan, Krisis Air, Dan Polusi Bukan Hanya Soal Teknis, Tapi Soal Cara Berpikir Dan Cara Hidup.

Sebagai Sustainability Guide, Raka Tidak Ikut Bertarung Atau Bersembunyi Dalam Konflik Antar Peran. Ia Adalah Penuntun. Di Sepanjang Permainan, Ia Membantu Memastikan Nilai-Nilai Luhur Seperti Gotong Royong (Sabilulungan) Dan Peduli Sesama (Silih Asah, Silih Asih, Silih Asuh) Tetap Terjaga Hidup.

Ia Tidak Bisa Mengubah Jalan Cerita, Tapi Bisa Menuntun Arah Langkah. Ia Tahu Bahwa Perubahan Besar Selalu Dimulai Dari Satu Hal Sederhana Yaitu Kesadaran.`
      }
    };

    const data = roleData[role] || roleData['SUSTAINABILITY GUIDE'];

    return (
      <div className="bg-white rounded-2xl p-6 shadow-lg">
        <p className="text-gray-800 text-sm leading-relaxed">
          {data.description}
        </p>
      </div>
    );
  };

  return (
    <div 
      className="min-h-screen relative"
      style={{
        backgroundImage: 'url(/assets/images/backgrounds/signup-bg.jpg)',
        backgroundSize: '100% 100%',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}
    >
      {/* Header */}
      <header className="bg-gradient-to-r from-green-600 to-green-800 text-white p-4 shadow-lg">
        <div className="max-w-7xl mx-auto flex items-center justify-between">
          <Logo />
          <button className="p-2 hover:bg-green-700 rounded-lg transition-colors duration-200">
            <div className="w-6 h-6 flex flex-col justify-center space-y-1">
              <div className="w-full h-0.5 bg-white"></div>
              <div className="w-full h-0.5 bg-white"></div>
              <div className="w-full h-0.5 bg-white"></div>
            </div>
          </button>
        </div>
      </header>

      {/* Main Content */}
      <main className="relative z-10 max-w-7xl mx-auto px-4 py-8">
        {/* Phase Header */}
        <section className="mb-8">
          <div className="text-center">
            <div className="bg-gradient-to-r from-yellow-400 to-orange-400 text-white font-bold py-4 px-8 rounded-2xl shadow-lg inline-block">
              <h1 className="text-2xl">PERANMU ADALAH...</h1>
            </div>
          </div>
        </section>

        {/* Main Game Area */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Role Card */}
          <div className="flex justify-center">
            <RoleCard role={currentRole} />
          </div>

          {/* Role Description */}
          <div>
            <RoleDescription role={currentRole} />
          </div>
        </div>

        {/* Room Code and Generate Button */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-8">
          {/* Room Code Display */}
          <div className="bg-white rounded-2xl p-6 shadow-lg">
            <div className="text-center">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Kode Ruangan</h3>
              <div className="bg-gray-100 rounded-xl p-4 mb-4">
                <span className="text-3xl font-bold text-gray-800 tracking-wider">
                  {roomCode}
                </span>
              </div>
              <p className="text-sm text-gray-600">
                Bagikan kode ini kepada pemain lain untuk bergabung
              </p>
            </div>
          </div>

          {/* Generate Code Button */}
          <div className="bg-white rounded-2xl p-6 shadow-lg">
            <div className="text-center">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Generate Kode Baru</h3>
              <button
                onClick={generateNewCode}
                className="bg-gradient-to-r from-red-600 to-red-700 hover:from-red-700 hover:to-red-800 text-white font-bold py-4 px-8 rounded-2xl shadow-lg transition-all duration-300 transform hover:scale-105"
              >
                GENERATE<br />KODE
              </button>
              {generatedCode && (
                <p className="text-sm text-gray-600 mt-3">
                  Kode baru: <span className="font-bold">{generatedCode}</span>
                </p>
              )}
            </div>
          </div>
        </div>

        
      </main>
    </div>
  );
};

export default ModeratorRoom;
