import React from "react";
import Header from "../components/common/Header";

const NightPhasePage = () => {
  return (
    <div
      className="min-h-screen relative"
      style={{
        backgroundColor: "#EDFDC1",
        backgroundImage:
          "url(/assets/images/backgrounds/Background_atas.png), url(/assets/images/backgrounds/Background_bawah.png)",
        backgroundPosition: "top center, bottom center",
        backgroundRepeat: "no-repeat, no-repeat",
        backgroundSize: "100% auto, 100% auto",
      }}
    >
      <Header />

      {/* Night Phase Content */}
      <div
        className="flex flex-col items-center justify-center relative"
        style={{ height: "calc(100vh - 100px)" }}
      >
        {/* Night Phase Image */}
        <div className="relative">
          <img
            src="/assets/images/icons/nightphase.png"
            alt="Night Phase"
            className="w-auto h-auto max-w-lg max-h-96 object-contain"
          />

          {/* Phase Title Overlay */}
          <div
            className="absolute inset-0 flex items-center justify-center"
            style={{ transform: "translateY(40px)" }}
          >
            <h1 className="text-white text-5xl font-bold font-poppins text-center">
              Fase Malam
            </h1>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NightPhasePage;
