import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/common/Header";
import Background from "../components/common/Background";
import axios from "axios";
import Modal from "../components/common/Modal";

const LandingPage = () => {
  const navigate = useNavigate();

  const [news, setNews] = useState([]);
  const [characters, setCharacters] = useState([]);
  const [selectedCharacter, setSelectedCharacter] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:3000/api/learning-materials/latest")
      .then(res => {
        setNews(res.data);
      });
    axios.get("http://localhost:3000/api/characters")
      .then(res => {
        setCharacters(res.data);
      });
  }, []);

  const handleCharacterClick = (char) => {
    setSelectedCharacter(char);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedCharacter(null);
  };

  const handlePlayClick = () => {
    const playerId = 8;
    if (parseInt(playerId) === 8) {
      navigate("/moderator-room");
    } else {
      navigate(`/role-explanation-room/${playerId}`);
    }
  };


  // List of character roles for QR login (from DB)
  const characterLinks = characters.map(char => ({ name: char.displayName || char.name, role: char.roleType, char }));

  return (
    <Background>
      <Header />
      <main className="relative z-10 flex flex-col items-center justify-center min-h-[80vh] px-4">
        <div className="mb-8">
          <h2 className="text-xl font-bold text-center text-white">Login QR (Role):</h2>
          <div className="flex flex-wrap gap-4 justify-center mt-4">
            {characterLinks.map((char, idx) => (
              <div key={idx} className="flex flex-col items-center">
                <a
                  href={`/login?role=${char.role}`}
                  className="px-6 py-3 bg-yellow-500 text-black font-bold rounded-xl shadow-lg hover:bg-yellow-600 transition mb-2"
                >
                  {char.name}
                </a>
                <button
                  className="px-4 py-1 bg-blue-500 text-white rounded-lg font-bold hover:bg-blue-700 transition"
                  onClick={() => handleCharacterClick(char.char)}
                >
                  Lihat Penjelasan
                </button>
              </div>
            ))}
          </div>
        {/* Character Modal */}
        <Modal isOpen={showModal} onClose={closeModal} title={selectedCharacter?.displayName || selectedCharacter?.name} size="md">
          {selectedCharacter && (
            <div className="space-y-4">
              {selectedCharacter.cardImage && (
                <img src={selectedCharacter.cardImage} alt={selectedCharacter.displayName} className="w-full max-w-xs mx-auto rounded-xl" />
              )}
              <div>
                <strong>Role:</strong> {selectedCharacter.roleType}
              </div>
              <div>
                <strong>Deskripsi:</strong> {selectedCharacter.description}
              </div>
              {selectedCharacter.ability && (
                <div>
                  <strong>Ability:</strong> {selectedCharacter.ability}
                </div>
              )}
            </div>
          )}
        </Modal>
        </div>

        {/* News Section */}
        <div className="mb-8 w-full max-w-2xl mx-auto">
          <h2 className="text-xl font-bold text-center text-white mb-4">Berita & Video Terkini</h2>
          <ul className="bg-white rounded-xl shadow-lg p-6 divide-y divide-gray-200">
            {news.map((item, idx) => (
              <li key={item._id || idx} className="py-3 flex items-center justify-between">
                <span className="font-semibold text-gray-800">{item.title}</span>
                <a
                  href={item.type === "article" ? item.originalUrl : item.videoUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="ml-4 px-4 py-2 bg-blue-600 text-white rounded-lg font-bold hover:bg-blue-700 transition"
                >
                  Baca/Watch
                </a>
              </li>
            ))}
          </ul>
        </div>

        {/* ...existing code... */}
        <div className="relative flex justify-center">
          <img
            src="/assets/images/container/play-container.png"
            alt="Play Container"
            className="w-auto h-auto max-w-2xl"
          />
          <div className="absolute bottom-16 left-1/2 transform -translate-x-1/2">
            <button onClick={handlePlayClick} className="relative group">
              <div className="absolute top-1.5 left-0 w-full h-full bg-red-900 rounded-xl"></div>
              <div
                className="relative px-8 py-3 text-white font-bold text-lg rounded-xl transition-all duration-200 transform group-hover:translate-y-1 group-active:translate-y-2 flex items-center gap-3 shadow-lg"
                style={{ backgroundColor: "#982827" }}
              >
                <div className="w-0 h-0 border-l-[12px] border-l-white border-t-[8px] border-t-transparent border-b-[8px] border-b-transparent"></div>
                <span className="font-bold tracking-wide"
                  style={{ fontFamily: "Nunito, sans-serif" }}
                >
                  PLAY
                </span>
              </div>
            </button>
          </div>
        </div>
      </main>
    </Background>
  );
};

export default LandingPage;
