import React, { useState } from 'react';

const ChooseTopicCardPage = () => {
  const [selectedCard, setSelectedCard] = useState(null);

  const cards = [
    { id: 1, number: '1', topic: 'UDARA', image: 'card1air.png' },
    { id: 2, number: '2', topic: 'HUTAN', image: 'card2hutan.png' },
    { id: 3, number: '3', topic: 'AIR', image: 'card3air.png' },
    { id: 4, number: '4', topic: 'SAMPAH', image: 'card4sampah.png' },
    { id: 5, number: '5', topic: 'UDARA', image: 'card5udara.png' },
    { id: 6, number: '6', topic: 'UDARA', image: 'card6udara.png' },
    { id: 7, number: '7', topic: 'UDARA', image: 'card7udara.png' },
    { id: 8, number: '8', topic: 'UDARA', image: 'card8udara.png' }
  ];

  const handleCardClick = (cardId) => {
    setSelectedCard(cardId);
    // Add navigation or action logic here
    console.log(`Selected card: ${cardId}`);
  };

  return (
    <div 
      className="min-h-screen relative"
      style={{
        backgroundImage: 'url(/assets/images/backgrounds/signup-bg.png)',
        backgroundSize: '100% 100%',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}
    >
      {/* Header */}
      <header className="w-full relative">
        <img
          src="/assets/images/navbar/navbar.png"
          alt="EcoSCha Navbar"
          className="w-full h-auto object-cover shadow-lg"
        />
        {/* Hamburger Menu Button */}
        <button className="absolute top-4 right-4 p-2 hover:bg-black/10 rounded-lg transition-colors duration-200 hover:scale-105 transform">
          <img
            src="/assets/images/icons/hamburgerbutton.png"
            alt="Menu"
            className="w-12 h-12 transition-transform duration-200 hover:brightness-110"
          />
        </button>
      </header>

      {/* Main Content */}
      <main className="relative z-10 max-w-4xl mx-auto px-4 py-6">
        {/* Content Container */}
        <div className="bg-light-gray rounded-3xl shadow-2xl p-6">
          {/* Title */}
          <div className="text-center mb-6">
            <div className="bg-yellow text-white font-bold py-3 px-6 rounded-2xl shadow-lg inline-block text-xl">
              PILIH SALAH SATU KARTU PERTANYAAN
            </div>
          </div>

          {/* Cards Grid */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-3 max-w-1xl mx-auto">
            {cards.map((card) => (
              <button
                key={card.id}
                onClick={() => handleCardClick(card.id)}
                className={`transition-all duration-300 transform hover:scale-105 hover:shadow-xl ${
                  selectedCard === card.id ? 'ring-4 ring-yellow scale-105' : ''
                } rounded-lg overflow-hidden`}
              >
                <img
                  src={`/assets/images/environmental-card/${card.image}`}
                  alt={`Card ${card.number} - ${card.topic}`}
                  className="w-full h-auto object-contain"
                />
              </button>
            ))}
          </div>

          {/* Selected Card Preview */}
          {selectedCard && (
            <div className="mt-6 text-center">
              <div className="bg-white rounded-2xl p-4 shadow-lg inline-block max-w-sm">
                <img
                  src={`/assets/images/environmental-card/${cards.find(c => c.id === selectedCard)?.image}`}
                  alt={`Card ${selectedCard}`}
                  className="w-full h-auto rounded-xl mb-3"
                />
                <p className="text-dark-green font-bold text-base">
                  Kartu {selectedCard} - {cards.find(c => c.id === selectedCard)?.topic}
                </p>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default ChooseTopicCardPage;
