import React, { useState } from 'react';

const WasteVillainPage = () => {
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [currentAction, setCurrentAction] = useState('eliminate'); // 'eliminate', 'diselematkan', 'rolenya'
  const [timer, setTimer] = useState('01:00');

  const players = [
    { id: 1, name: 'Player 1', isAlive: true },
    { id: 2, name: 'Player 2', isAlive: true },
    { id: 3, name: 'Player 3', isAlive: true },
    { id: 4, name: 'Player 4', isAlive: true },
    { id: 5, name: 'Player 5', isAlive: true },
    { id: 6, name: 'Player 6', isAlive: true },
    { id: 7, name: 'Player 7', isAlive: true },
    { id: 8, name: 'Player 8', isAlive: true },
  ];

  const actionTexts = {
    eliminate: 'Pilih satu warga yang akan dieliminasi:',
    diselematkan: 'Pilih satu warga yang ingin diselamatkan:',
    rolenya: 'Pilih satu warga yang ingin dilihat rolenya:'
  };

  const handlePlayerSelect = (playerId) => {
    setSelectedPlayer(playerId);
  };

  const handleConfirm = () => {
    if (selectedPlayer) {
      console.log(`Selected player ${selectedPlayer} for action: ${currentAction}`);
      // Handle the action based on currentAction
    }
  };

  return (
    <div 
      className="min-h-screen relative"
      style={{
        backgroundImage: 'url(/assets/images/backgrounds/signup-bg.png)',
        backgroundSize: '100% 100%',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}
    >
      {/* Header */}
      <header className="w-full relative">
        <img
          src="/assets/images/navbar/navbar.png"
          alt="EcoSCha Navbar"
          className="w-full h-auto object-cover shadow-lg"
        />
        {/* Hamburger Menu Button */}
        <button className="absolute top-4 right-4 p-2 hover:bg-black/10 rounded-lg transition-colors duration-200 hover:scale-105 transform">
          <img
            src="/assets/images/icons/hamburgerbutton.png"
            alt="Menu"
            className="w-12 h-12 transition-transform duration-200 hover:brightness-110"
          />
        </button>
      </header>

      {/* Main Content */}
      <main className="relative z-10 max-w-4xl mx-auto px-4 py-8">
        
        {/* Top Section */}
        <div className="flex justify-between items-center mb-6">
          {/* Night Badge */}
          <div className="bg-yellow text-white font-bold py-2 px-6 rounded-4xl shadow-lg text-lg">
            MALAM 1
          </div>
          
          {/* Timer */}
          <div 
            className="text-white font-bold py-2 px-4 rounded-lg text-lg"
            style={{ backgroundColor: '#982827' }}
          >
            {timer}
          </div>
        </div>

        {/* Instruction Card */}
        <div className="bg-white rounded-2xl shadow-lg p-4 mb-6">
          <div className="text-gray-800 text-base font-poppins">
            {actionTexts[currentAction]}
          </div>
        </div>

        {/* Players Grid */}
        <div className="grid grid-cols-4 gap-4 mb-8">
          {players.map((player) => (
            <button
              key={player.id}
              onClick={() => handlePlayerSelect(player.id)}
              className={`
                font-bold py-3 px-4 rounded-xl shadow-lg transition-all duration-300 transform hover:scale-105 text-white
                ${selectedPlayer === player.id 
                  ? 'ring-4 ring-yellow-400 scale-105' 
                  : ''
                }
                ${player.id === 2 || player.id === 7 
                  ? 'bg-yellow hover:bg-yellow-600' 
                  : 'hover:bg-red-700'
                }
              `}
              style={{ 
                backgroundColor: player.id === 2 || player.id === 7 ? '#f0be01' : '#982827',
                fontFamily: 'Poppins, sans-serif'
              }}
            >
              {player.name}
            </button>
          ))}
        </div>

        {/* OK Button */}
        <div className="text-center">
          <button
            onClick={handleConfirm}
            disabled={!selectedPlayer}
            className={`
              text-white font-bold py-3 px-8 rounded-2xl shadow-lg transition-all duration-300 transform hover:scale-105
              ${selectedPlayer ? 'hover:bg-red-700' : 'opacity-50 cursor-not-allowed'}
            `}
            style={{ 
              backgroundColor: '#982827',
              fontFamily: 'Poppins, sans-serif'
            }}
          >
            OK
          </button>
        </div>
      </main>
    </div>
  );
};

export default WasteVillainPage;
