const NewsCard = ({ news }) => (
    <div className="bg-white rounded-xl p-4 shadow-md hover:shadow-lg transition-shadow duration-300">
      <div className="flex items-start space-x-3">
        <div className="w-12 h-12 bg-gradient-to-br from-green-400 to-green-600 rounded-lg flex items-center justify-center text-white font-bold text-sm">
          {news.category.split(' ')[0]}
        </div>
        <div className="flex-1">
          <h3 className="font-semibold text-gray-800 text-sm mb-1">{news.title}</h3>
          <p className="text-gray-600 text-xs mb-2">{news.summary}</p>
          <div className="flex justify-between items-center">
            <span className="text-xs text-green-600 font-medium">{news.category}</span>
            <span className="text-xs text-gray-400">{news.date}</span>
          </div>
        </div>
      </div>
    </div>
  );import React from 'react';

const HomePage = () => {
  
  // Character roles data
  const characterRoles = [
    {
      id: 1,
      name: "WASTE MANAGER",
      description: "Bertanggung jawab mengelola dan mengidentifikasi masalah sampah",
      image: "/assets/images/cards/waste-manager.jpg",
      team: "good"
    },
    {
      id: 2,
      name: "SUSTAINABILITY GUIDE",
      description: "Pemandu yang mengarahkan diskusi dan solusi berkelanjutan",
      image: "/assets/images/cards/waste-manager.jpg",
      team: "neutral"
    },
    {
      id: 3,
      name: "ECO CITIZEN",
      description: "Warga peduli lingkungan yang aktif dalam aksi pro-lingkungan",
      image: "/assets/images/cards/waste-manager.jpg",
      team: "good"
    },
    {
      id: 4,
      name: "GREEN GUARDIAN",
      description: "Penjaga lingkungan yang melindungi ekosistem dari kerusakan",
      image: "/assets/images/cards/waste-manager.jpg",
      team: "good"
    },
    {
      id: 5,
      name: "WASTE VILLAIN",
      description: "Karakter yang merusak lingkungan secara diam-diam",
      image: "/assets/images/cards/waste-villain.png",
      team: "bad"
    }
  ];

  const Logo = () => (
    <div className="inline-flex items-center justify-center">
      <img 
        src="/assets/images/logo/logo_ecoscha.png" 
        alt="EcoSCha Logo" 
        className="h-10 w-auto hover:scale-105 transition-transform duration-200"
        onError={(e) => {
          // Fallback jika gambar tidak ditemukan
          e.target.style.display = 'none';
          e.target.nextSibling.style.display = 'inline-flex';
        }}
      />
    </div>
  );

  const CharacterCard = ({ character }) => (
    <div className="bg-gradient-to-b from-yellow-400 to-orange-400 rounded-2xl p-4 shadow-lg transform hover:scale-105 transition-transform duration-300">
      <div className="bg-white rounded-xl p-4 mb-3">
        <div className="text-center">
          <div className="text-xs text-green-600 mb-2">+ EcoSCha +</div>
          <div className="w-20 h-24 bg-gray-200 rounded-lg mx-auto mb-2 flex items-center justify-center overflow-hidden">
            {/* Character illustration */}
            <img 
              src={character.image} 
              alt={character.name}
              className="w-full h-full object-cover rounded-lg"
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'flex';
              }}
            />
            <div className="w-16 h-20 bg-gradient-to-b from-amber-100 to-amber-200 rounded-lg flex items-center justify-center" style={{display: 'none'}}>
              <div className="text-2xl">ðŸ‘¤</div>
            </div>
          </div>
        </div>
      </div>
      
      <div className="bg-gradient-to-r from-green-700 to-green-900 text-white text-center py-2 rounded-xl">
        <div className="font-bold text-sm">{character.name}</div>
      </div>
    </div>
  );

  const NewsCard = ({ news }) => (
    <div className="bg-white rounded-xl p-4 shadow-md hover:shadow-lg transition-shadow duration-300">
      <div className="flex items-start space-x-3">
        <div className="w-12 h-12 bg-gradient-to-br from-green-400 to-green-600 rounded-lg flex items-center justify-center text-white font-bold text-sm">
          {news.category.split(' ')[0]}
        </div>
        <div className="flex-1">
          <h3 className="font-semibold text-gray-800 text-sm mb-1">{news.title}</h3>
          <p className="text-gray-600 text-xs mb-2">{news.summary}</p>
          <div className="flex justify-between items-center">
            <span className="text-xs text-green-600 font-medium">{news.category}</span>
            <span className="text-xs text-gray-400">{news.date}</span>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-100 via-yellow-50 to-green-200 relative">
      {/* Header */}
      <header className="bg-gradient-to-r from-green-600 to-green-800 text-white p-4 shadow-lg">
        <div className="max-w-6xl mx-auto flex items-center justify-between">
          <Logo />
          <button className="p-2 hover:bg-green-700 rounded-lg transition-colors duration-200">
            <div className="w-6 h-6 flex flex-col justify-center space-y-1">
              <div className="w-full h-0.5 bg-white"></div>
              <div className="w-full h-0.5 bg-white"></div>
              <div className="w-full h-0.5 bg-white"></div>
            </div>
          </button>
        </div>
      </header>

      {/* Background Pattern - Bottom */}
      <div className="absolute bottom-0 left-0 w-full h-20 bg-gradient-to-r from-yellow-400 to-orange-400">
        <div className="flex h-full">
          {Array.from({ length: 25 }, (_, i) => (
            <div key={i} className="flex-1 flex flex-col">
              {Array.from({ length: 3 }, (_, j) => (
                <div 
                  key={j} 
                  className={`flex-1 ${(i + j) % 2 === 0 ? 'bg-yellow-400' : 'bg-orange-300'}`}
                />
              ))}
            </div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <main className="relative z-10 max-w-6xl mx-auto px-4 py-8 pb-24">
        {/* Game Description Section */}
        <section className="mb-8">
          <div className="bg-white rounded-2xl shadow-lg p-6">
            <div className="flex items-start space-x-6">
              <div className="flex-shrink-0">
                <img 
                  src="/assets/images/logo/ecoscha-logo.svg" 
                  alt="EcoSCha Logo" 
                  className="h-16 w-auto"
                  onError={(e) => {
                    // Fallback jika gambar tidak ditemukan
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
                {/* Fallback Logo jika gambar tidak ada */}
                <div className="flex items-center justify-center w-24 h-16 bg-gradient-to-br from-green-400 to-green-600 rounded-xl" style={{display: 'none'}}>
                  <span className="text-white font-bold text-lg">ðŸŒ± ECO</span>
                </div>
              </div>
              <div className="flex-1">
                <h1 className="text-xl font-bold text-gray-800 mb-4">
                  EcoSCha Card (Environmental Issues Social Character)
                </h1>
                <p className="text-gray-700 text-sm leading-relaxed">
                  adalah permainan edukatif berbasis kartu role-play yang dirancang untuk 
                  meningkatkan pemahaman dan kepedulian terhadap isu-isu lingkungan. Permainan ini dirancang untuk 
                  membangun pemahaman kritis, kepedulian sosial, serta kemampuan bekerja sama dalam 
                  menyelesaikan permasalahan lingkungan yang kontekstual, baik di tingkat 
                  lokal maupun global. Permainan ini menghadirkan simulasi situasi nyata yang menjadi permasalahan lingkungan, 
                  dimana pemain mengambil peran sebagai tokoh-tokoh yang 
                  merepresentasikan berbagai perspektif dalam menghadapi persoalan lingkungan.
                </p>
              </div>
            </div>
          </div>
        </section>

        {/* Character Roles Section */}
        <section className="mb-8">
          <div className="bg-white rounded-2xl shadow-lg p-6">
            <div className="text-center mb-6">
              <button className="bg-gradient-to-r from-yellow-400 to-orange-400 text-white font-bold py-3 px-8 rounded-2xl shadow-lg hover:from-yellow-500 hover:to-orange-500 transition-all duration-300 transform hover:scale-105">
                DAFTAR PERAN
              </button>
            </div>
            
            <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-5 gap-4">
              {characterRoles.map((character) => (
                <CharacterCard key={character.id} character={character} />
              ))}
            </div>
            
            {/* Second row for duplicate cards as shown in design */}
            <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-4 gap-4 mt-4">
              {characterRoles.slice(0, 4).map((character, index) => (
                <CharacterCard key={`duplicate-${character.id}`} character={character} />
              ))}
            </div>
          </div>
        </section>

        {/* News Section */}
        <section className="mb-8">
          <div className="bg-white rounded-2xl shadow-lg p-6">
            <div className="text-center mb-6">
              <button className="bg-gradient-to-r from-yellow-400 to-orange-400 text-white font-bold py-3 px-8 rounded-2xl shadow-lg">
                BERITA HARI INI
              </button>
            </div>
            
            {/* Empty state untuk berita */}
            <div className="h-64 flex items-center justify-center text-gray-400">
              <div className="text-center">
                <div className="text-4xl mb-4">ðŸ“°</div>
                <p>Berita akan ditampilkan di sini</p>
              </div>
            </div>
          </div>
        </section>

        {/* Call to Action */}
        <section className="text-center">
          <div className="bg-gradient-to-r from-green-600 to-green-800 text-white rounded-2xl shadow-lg p-8">
            <h2 className="text-2xl font-bold mb-4">Siap Bermain EcoSCha Card?</h2>
            <p className="text-green-100 mb-6">
              Bergabunglah dengan pemain lain dan mulai petualangan menyelamatkan lingkungan!
            </p>
            <div className="space-x-4">
              <button className="bg-yellow-400 hover:bg-yellow-500 text-green-800 font-bold py-3 px-8 rounded-xl transition-colors duration-200 transform hover:scale-105">
                Mulai Bermain
              </button>
              <button className="border-2 border-white text-white hover:bg-white hover:text-green-800 font-bold py-3 px-8 rounded-xl transition-all duration-200">
                Pelajari Aturan
              </button>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
};

export default HomePage;
