import React, { useState, useEffect } from 'react';

const ModeratorRoom = () => {
  const [roomCode, setRoomCode] = useState('...');
  const [currentRole, setCurrentRole] = useState('SUSTAINABILITY GUIDE');
  const [generatedCode, setGeneratedCode] = useState('');

  const Logo = () => (
    <div className="inline-flex items-center justify-center">
      <img 
        src="/assets/images/logo/ecoscha-logo.png" 
        alt="EcoSCha Logo"
        className="h-12 w-auto"
      />
    </div>
  );

  const generateNewCode = () => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let result = '';
    for (let i = 0; i < 8; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    setGeneratedCode(result);
    setRoomCode(result);
  };

  const RoleCard = ({ role }) => {
    const roleData = {
      'SUSTAINABILITY GUIDE': {
        image: '/assets/images/icons/moderator-role.png',
        description: `Raka Adalah Sosok Yang Dituakan Di Desa Dayeuhmangguung. Ia Bukan Kepala Desa Secara Jabatan, Tetapi Pangaguing Lembur Penuntun Masyarakat Dalam Nilai-Nilai Kahirupan (Kehidupan) Dan Kelestarian Alam. Sejak Muda, Ia Belajar Banyak Dari Karuhun (Leluhur) Tentang Pentingnya Menjaga Keseimbangan Antara Manusia Dan Alam.

"Alam TÃ©h Sakola Nu Teu Boga Tembok," Katanya Suatu Hari Sambil Mengajarkan Anak-Anak Menanam Pohon Di Lereng Gunung Cikuray. (Alam Itu Sekolah Tanpa Dinding.) Ia Meyakini Bahwa Masalah Seperti Kebakaran Hutan, Krisis Air, Dan Polusi Bukan Hanya Soal Teknis, Tapi Soal Cara Berpikir Dan Cara Hidup.

Sebagai Sustainability Guide, Raka Tidak Ikut Bertarung Atau Bersembunyi Dalam Konflik Antar Peran. Ia Adalah Penuntun. Di Sepanjang Permainan, Ia Membantu Memastikan Nilai-Nilai Luhur Seperti Gotong Royong (Sabilulungan) Dan Peduli Sesama (Silih Asah, Silih Asih, Silih Asuh) Tetap Terjaga Hidup.

Ia Tidak Bisa Mengubah Jalan Cerita, Tapi Bisa Menuntun Arah Langkah. Ia Tahu Bahwa Perubahan Besar Selalu Dimulai Dari Satu Hal Sederhana Yaitu Kesadaran.`
      }
    };

    const data = roleData[role] || roleData['SUSTAINABILITY GUIDE'];

    return (
      <div className="flex justify-center">
        <img 
          src={data.image} 
          alt={role}
          className="w-auto h-auto max-w-full"
        />
      </div>
    );
  };

  const RoleDescription = ({ role }) => {
    return (
      <div className="w-full">
        <img 
          src="/assets/images/icons/role-desc.png" 
          alt="Role Description"
          className="w-full h-auto rounded-2xl shadow-lg"
        />
      </div>
    );
  };


  return (
    <div 
      className="min-h-screen relative"
      style={{
        backgroundImage: 'url(/assets/images/backgrounds/signup-bg.png)',
        backgroundSize: '100% 100%',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}
    >
      {/* Header */}
      <header className="w-full relative">
        <img
          src="/assets/images/navbar/navbar.png"
          alt="EcoSCha Navbar"
          className="w-full h-auto object-cover shadow-lg"
        />
        {/* Hamburger Menu Button */}
        <button className="absolute top-4 right-4 p-2 hover:bg-black/10 rounded-lg transition-colors duration-200 hover:scale-105 transform">
          <img
            src="/assets/images/icons/hamburgerbutton.png"
            alt="Menu"
            className="w-12 h-12 transition-transform duration-200 hover:brightness-110"
          />
        </button>
      </header>

      {/* Main Content */}
      <main className="relative z-10 max-w-7xl mx-auto px-4 py-8">
        {/* Phase Header */}
        <section className="mb-8">
          <div className="text-center">
            <div className="text-white font-bold py-4 px-8 rounded-2xl shadow-lg inline-block" style={{ backgroundColor: '#F0BE01' }}>
              <h1 className="text-2xl">PERANMU ADALAH...</h1>
            </div>
          </div>
        </section>

// ...existing code...

        {/* Main Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Left Column: Role Card */}
          <div className="flex justify-center">
            <RoleCard role={currentRole} />
          </div>

          {/* Right Column: Role Description + Bottom Elements */}
          <div className="space-y-6">
            {/* Role Description */}
            <div>
              <RoleDescription role={currentRole} />
            </div>

            {/* Room Code and Generate Button Row */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Room Code Display */}
              <div className="text-center">
                <div className="bg-gray-100 rounded-xl p-6 shadow-lg">
                  <span className="text-3xl font-bold text-gray-800 tracking-wider">
                    {roomCode}
                  </span>
                </div>
              </div>

              {/* Generate Code Button */}
              <div className="flex items-center justify-center">
                <button
                  onClick={generateNewCode}
                  className="text-white font-bold py-4 px-8 rounded-2xl shadow-lg transition-all duration-300 transform hover:scale-105"
                  style={{ 
                    backgroundColor: '#982827',
                    fontFamily: 'Nunito, sans-serif'
                  }}
                  onMouseEnter={(e) => e.target.style.backgroundColor = '#7a1f1e'}
                  onMouseLeave={(e) => e.target.style.backgroundColor = '#982827'}
                >
                  GENERATE<br />KODE
                </button>
              </div>
            </div>
          </div>
        </div>

// ...existing code...
        {/* Generated Code Feedback */}
        {generatedCode && (
          <div className="mt-6 text-center">
            <div className="bg-white rounded-xl p-4 shadow-lg inline-block">
              <p className="text-lg text-gray-800">
                Kode baru: <span className="font-bold text-green-600">{generatedCode}</span>
              </p>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default ModeratorRoom;
