import React, { useState } from 'react';

const ModeratorNightPhasePage = () => {
  const [currentNight, setCurrentNight] = useState(1);
  const [currentStep, setCurrentStep] = useState(1); // Step 1: Deskripsi, Step 2: Instruksi

  const nightData = {
    1: {
      title: "MALAM ...",
      description: "Warga-warga Eco-Village, malam telah tiba. Langit di atas sana terasa bersih dan dipenuhi bintang, menandakan malam ini adalah malam yang tenang. Kita semua baru saja melewati hari yang produktif. Pepohonan yang kita tanam mulai tumbuh, dan udara terasa sejuk. Namun, meskipun damai, kita harus tetap waspada. Waktu untuk beristirahat telah tiba. Pejamkan mata kalian, biarkan diri kalian hanyut dalam lelap.",
      instructions: [
        "1. Waste Villain, buka mata kalian dan pilih satu warga yang akan dieliminasi malam ini.",
        "2. Green Guardian, buka mata kalian dan pilih satu warga yang ingin diselamatkan malam ini.",
        "3. Waste Manager, buka mata kalian dan pilih satu warga yang ingin dilihat rolenya malam ini."
      ]
    }
  };

  const handleContinue = () => {
    if (currentStep === 1) {
      // Pindah ke step 2 (instruksi)
      setCurrentStep(2);
    } else {
      // Pindah ke malam berikutnya atau fase berikutnya
      if (currentNight < Object.keys(nightData).length) {
        setCurrentNight(currentNight + 1);
        setCurrentStep(1);
      } else {
        // Navigate to next phase
        console.log("Navigate to day phase");
      }
    }
  };

  return (
    <div 
      className="min-h-screen relative"
      style={{
        backgroundImage: 'url(/assets/images/backgrounds/signup-bg.png)',
        backgroundSize: '100% 100%',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}
    >
      {/* Header */}
      <header className="w-full relative">
        <img
          src="/assets/images/navbar/navbar.png"
          alt="EcoSCha Navbar"
          className="w-full h-auto object-cover shadow-lg"
        />
        {/* Hamburger Menu Button */}
        <button className="absolute top-4 right-4 p-2 hover:bg-black/10 rounded-lg transition-colors duration-200 hover:scale-105 transform">
          <img
            src="/assets/images/icons/hamburgerbutton.png"
            alt="Menu"
            className="w-12 h-12 transition-transform duration-200 hover:brightness-110"
          />
        </button>
      </header>

      {/* Main Content */}
      <main className="relative z-10 max-w-4xl mx-auto px-4 py-16">

        {/* Phase Title */}
        <div className="text-center mb-8 relative">
          {/* Background Image */}
          <div 
            className="inline-block transform scale-150"
            style={{
              backgroundImage: 'url(/assets/images/icons/moderator-night-phase.png)',
              backgroundSize: 'contain',
              backgroundPosition: 'center',
              backgroundRepeat: 'no-repeat',
              width: '300px',
              height: '80px'
            }}
          >
          </div>
          
          {/* Text Overlay */}
          <div 
            className="absolute inset-0 flex items-center justify-center"
            style={{ 
              top: '50%',
              transform: 'scale(0.67)'
            }}
          >
            <span className="text-white font-bold text-4xl">
              Fase Malam
            </span>
          </div>
        </div>

        {/* Content Card */}
        <div className="bg-white rounded-3xl shadow-2xl p-6 mb-8">
          {/* Night Number */}
          <div className="mb-4">
            <div className="bg-yellow text-white font-bold py-2 px-6 rounded-2xl shadow-lg inline-block text-lg">
              {nightData[currentNight].title}
            </div>
          </div>

          {/* Content based on current step */}
          <div className="text-gray-800 text-base leading-relaxed font-poppins">
            {currentStep === 1 ? (
              // Step 1: Description
              <div className="mb-6">
                {nightData[currentNight].description}
              </div>
            ) : (
              // Step 2: Instructions
              <div className="space-y-4">
                {nightData[currentNight].instructions.map((instruction, index) => (
                  <div key={index} className="mb-3">
                    {instruction}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Continue Button */}
        <div className="text-center">
          <button
            onClick={handleContinue}
            className="text-white font-bold py-3 px-8 rounded-2xl shadow-lg transition-all duration-300 transform hover:scale-105"
            style={{ 
              backgroundColor: '#982827',
              fontFamily: 'Nunito, sans-serif'
            }}
            onMouseEnter={(e) => e.target.style.backgroundColor = '#7a1f1e'}
            onMouseLeave={(e) => e.target.style.backgroundColor = '#982827'}
          >
            LANJUTKAN
          </button>
        </div>
      </main>
    </div>
  );
};

export default ModeratorNightPhasePage;
